import React from "react";

function InputArea(props) {
  return (
    <div className="form">
      <input type="text" value={props.newItem} onChange={props.handleChange}/>
      <button onClick={props.handleClick}>
        <span>Add</span>
      </button>
    </div>
  );
}

export default InputArea;
