import React, {useState} from "react";
import Todoitems from "./Todoitems";
function App() {
  const [list, setList]=useState([]);
  const [newItem, setnewItem]=useState("");
  function handleChange(event){
    setnewItem(event.target.value);
  }
  function handleClick(){
    setList(prevValue => {
      return [...prevValue,newItem];
    });
    setnewItem("");
  }

  function deleteItem(id){
    setList(prevValue => {
    return  prevValue.filter((item,index) => {
        return index!==id
      })
    })
  }





  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <InputArea handleChange={handleChange} newItem={newItem} handleClick={handleClick} />
      <div>
        <ul>
        {list.map((curListItem,index) => (<Todoitems curListItem={curListItem} handleDelete={deleteItem} key={index} id={index}/>))}

        </ul>
      </div>
    </div>
  );
}

export default App;
